import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Container, Box, Grid, Paper, IconButton } from '@mui/material';
import SearchBar from '../components/SearchBar';
import BookList from '../components/BookList';
import ReadingList from '../components/ReadingList';
import axios from 'axios';
import MenuIcon from '@mui/icons-material/Menu'; // Importing menu icon

const LandingPage = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [readingList, setReadingList] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      const result = await axios({
        url: 'http://localhost:4000/',
        method: 'post',
        data: {
          query: `
            query Books {
              books {
                author
                coverPhotoURL
                readingLevel
                title
              }
            }
          `
        }
      });
      setBooks(result.data.data.books);
    };
    fetchBooks();
  }, []);

  const addBookToReadingList = book => {
    setReadingList([...readingList, book]);
  };

  const removeBookFromReadingList = bookToRemove => {
    setReadingList(readingList.filter(book => book!== bookToRemove));
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu">
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            My Book Library
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="lg">
        <Box sx={{ my: 2 }}>
          <SearchBar setSearchTerm={setSearchTerm} />
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <Paper elevation={2}>
                <BookList books={books.filter(book => book.title.includes(searchTerm))} addBookToReadingList={addBookToReadingList} />
              </Paper>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Paper elevation={2}>
                <ReadingList readingList={readingList} removeBookFromReadingList={removeBookFromReadingList} />
              </Paper>
            </Grid>
          </Grid>
        </Box>
      </Container>
    </>
  );
};

export default App;
