// SearchBar.js
import React, { useState } from 'react';
import { TextField, Box, CircularProgress, Autocomplete } from '@mui/material';
import axios from 'axios';

const SearchBar = ({ setSearchTerm }) => {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedResult, setSelectedResult] = useState(null); // State to manage selected result

  const handleSearch = async event => {
    const searchTerm = event.target.value.toLowerCase(); // Convert to lowercase for case-insensitive search
    setSearchTerm(searchTerm);

    setLoading(true);
    try {
      const result = await axios.get(`http://localhost:4000/search?query=${encodeURIComponent(searchTerm)}`);
      setSearchResults(result.data.books); // Assuming your API returns an array of books
    } catch (error) {
      console.error("Failed to fetch search results:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ textAlign: 'center', margin: '20px 0' }}>
      <Autocomplete
        options={searchResults}
        getOptionLabel={(option) => option.title} // Customize this based on your data structure
        renderInput={(params) => (
          <TextField
            {...params}
            label="Search books"
            variant="outlined"
            onChange={handleSearch}
            fullWidth
            InputProps={{
            ...(!loading && {
                endAdornment: (
                  <Box sx={{ pl: 2 }}>
                    {loading? <CircularProgress color="error" size={24} /> : null} {/* Changed color to steel blue */}
                  </Box>
                ),
              }),
            }}
            sx={{
              maxWidth: { xs: '90%', sm: '80%', md: '70%' }, // Responsive max-width
              mx: 'auto',
              borderWidth: 2, // Thicker border
              borderColor: 'divider', // Ensure the divider is visible
              '&.MuiOutlinedInput-root': {
                '& fieldset': {
                  borderColor: 'grey.500', // Border color
                },
                '&:hover fieldset': {
                  borderColor: 'grey.700', // Hover border color
                },
                '&.Mui-focused fieldset': {
                  borderColor: 'primary.main', // Focused border color
                },
              },
            }}
            onFocus={() => setSelectedResult(null)} // Reset selected result on focus
          />
        )}
        onInputChange={(event, value) => {
          handleSearch(event);
          setSelectedResult(null); // Reset selected result on input change
        }}
        renderOption={(props, option, { selected }) => (
          <li {...props}>
            {selected? <em>{option.title}</em> : option.title}
          </li>
        )}
        freeSolo // Allow users to enter values that aren't in the autocomplete options
        renderTags={(value, getTagProps) =>
          value.map((option, index) => (
            <React.Fragment key={index}>
              <span {...getTagProps({ index })}>
                {option.title}
              </span>
            </React.Fragment>
          ))
        }
      />
    </Box>
  );
};

export default SearchBar;
