import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Container, Box, Grid, Paper, IconButton, Menu, MenuItem, Button } from '@mui/material';
import SearchBar from './components/SearchBar';
import BookList from './components/BookList';
import ReadingList from './components/ReadingList';
import axios from 'axios';
import MenuIcon from '@mui/icons-material/Menu'; // Importing menu icon
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [readingList, setReadingList] = useState([]);
  const [anchorEl, setAnchorEl] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      const result = await axios({
        url: 'http://localhost:4000/graphql',
        method: 'post',
        data: {
          query: `
            query Books {
              books {
                author
                coverPhotoURL
                readingLevel
                title
              }
            }
          `
        }
      });
      setBooks(result.data.data.books);
    };
    fetchBooks();
  }, []);

  const addBookToReadingList = book => {
    setReadingList([...readingList, book]);
  };

  const removeBookFromReadingList = bookToRemove => {
    setReadingList(readingList.filter(book => book !== bookToRemove));
  };

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const goToReadingList = () => {
    navigate('/reading-list');
    handleMenuClose();
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={handleMenuClick}>
            <MenuIcon />
          </IconButton>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={goToReadingList}>Reading List</MenuItem>
          </Menu>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            My Book Library
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="lg">
        <Box sx={{ my: 2 }}>
          <SearchBar setSearchTerm={setSearchTerm} />
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Paper elevation={2}>
                <BookList books={books.filter(book => book.title.includes(searchTerm))} addBookToReadingList={addBookToReadingList} />
              </Paper>
            </Grid>
          </Grid>
        </Box>
      </Container>
      <Routes>
      <Route path="/" element={<HomePage navigate={navigate} />} />
      <Route path="/reading-list" element={<ReadingListPage readingList={readingList} removeBookFromReadingList={removeBookFromReadingList} />} />
    </Routes>
    </>
  );
};

export default App;
