import React, { useState, useEffect } from 'react';
import { AppBar, Toolbar, Typography, Container, Box, Grid, Paper, IconButton, Menu, MenuItem, Button, Snackbar } from '@mui/material';
import SearchBar from './components/SearchBar';
import BookList from './components/BookList';
import ReadingList from './components/ReadingList';
import axios from 'axios';
import MenuIcon from '@mui/icons-material/Menu';
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [books, setBooks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [readingList, setReadingList] = useState([]);
  const [anchorEl, setAnchorEl] = useState(null);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: '' }); // State for Snackbar
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBooks = async () => {
      const result = await axios({
        url: 'http://localhost:4000/graphql',
        method: 'post',
        data: {
          query: `
            query Books {
              books {
                author
                coverPhotoURL
                readingLevel
                title
              }
            }
          `
        }
      });
      setBooks(result.data.data.books);
    };
    fetchBooks();
  }, []);

  const addBookToReadingList = book => {
    setReadingList(prevState => [...prevState, book]); // Ensure state is updated correctly
    setSnackbar({ open: true, message: 'Added Successfully', severity: 'success' }); // Show success message with turquoise color
  };

  const removeBookFromReadingList = bookToRemove => {
    setReadingList(prevState => prevState.filter(book => book!== bookToRemove)); // Ensure state is updated correctly
    setSnackbar({ open: true, message: 'Removed Successfully', severity: 'warning' }); // Show success message with turquoise color
  };

  const handleMenuClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const goToReadingList = () => {
    navigate('/reading-list');
    handleMenuClose();
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={handleMenuClick}>
            <MenuIcon />
          </IconButton>
          <Menu
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={handleMenuClose}
          >
            <MenuItem onClick={goToReadingList}>Reading List</MenuItem>
          </Menu>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            My Book Library
          </Typography>
        </Toolbar>
      </AppBar>
      <Container maxWidth="lg">
        <Box sx={{ my: 2 }}>
          <SearchBar setSearchTerm={setSearchTerm} />
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Paper elevation={2}>
                <BookList books={books.filter(book => book.title.includes(searchTerm))} addBookToReadingList={addBookToReadingList} />
              </Paper>
            </Grid>
          </Grid>
        </Box>
      </Container>
      <Snackbar
        open={snackbar.open}
        autoHideDuration={3000}
        message={snackbar.message}
        severity={snackbar.severity}
        onClose={() => setSnackbar({...snackbar, open: false })}
        sx={{ backgroundColor: snackbar.severity === 'success'? '#40C4FF' : '#FFD700' }} // Customize Snackbar color based on severity
      />
    </>
  );
};

export default App;
