// SearchBar.js
import React, { useState } from 'react';
import { TextField, Box, CircularProgress, Autocomplete } from '@mui/material';
import axios from 'axios';

const SearchBar = ({ setSearchTerm }) => {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedResult, setSelectedResult] = useState(null); // State to manage selected result

  const handleSearch = async event => {
    const searchTerm = event.target.value.toLowerCase(); // Convert to lowercase for case-insensitive search
    setSearchTerm(searchTerm);

    setLoading(true);
    try {
      const result = await axios.get(`http://localhost:4000/search?query=${encodeURIComponent(searchTerm)}`);
      setSearchResults(result.data.books); // Assuming your API returns an array of books
    } catch (error) {
      console.error("Failed to fetch search results:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box sx={{ textAlign: 'center', margin: '20px 0' }}>
      <TextField
        label="Search books"
        variant="outlined"
        onChange={handleSearch}
        fullWidth
        InputProps={{
         ...(!loading && {
            endAdornment: (
              <Box sx={{ pl: 2 }}>
                {loading? <CircularProgress color="inherit" size={24} /> : null}
              </Box>
            ),
          }),
        }}
        onFocus={() => setSelectedResult(null)} // Reset selected result on focus
      />
    </Box>
  );
};

export default SearchBar;
